import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  email: string; // Declare the email property
  password: string; // Declare the password property

  constructor() { 
    this.email = '';
    this.password = '';
  }
  onSubmit(loginForm: NgForm) {

  }

}
<div class="container mt-5">
    <h1>Login</h1>
    <form (ngSubmit)="onSubmit()" #loginForm="ngForm">
        <div class="form-group">
            <label for="email">Email:</label>
            <input type="email" class="form-control" id="email" name="email" placeholder="Enter email"
                [(ngModel)]="email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$">
            <div *ngIf="loginForm.submitted && loginForm.controls.email.invalid" class="text-danger">
                Email is required and must be a valid email address.
            </div>
        </div>
        <div class="form-group">
            <label for="password">Password:</label>
            <input type="password" class="form-control" id="password" name="password" placeholder="Enter password"
                [(ngModel)]="password" required>
            <div *ngIf="loginForm.submitted && loginForm.controls.password.invalid" class="text-danger">
                Password is required.
            </div>
        </div>
        <button type="submit" class="btn btn-primary">Login</button>
    </form>
    <div class="mt-3">
        <p>Don't have an account? <a href="#">Sign up</a></p>
    </div>
</div>
